import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';
import classes from './AvailableMeals.module.css';
import {useState} from "react";

const [dummyMeals, setDummyMeals] = useState([])
const fetchMenuHandler = () => {
    fetch('https://react2-f5ff0-default-rtdb.europe-west1.firebasedatabase.app/DUMMY_MEALS.json').then((response) => {
        return response.json();
    }).then(
        (data) => {
            const transformedMeal = data.map(
                entry => {
                    return {
                        id: entry.id,
                        name: entry.name,
                        description: entry.description,
                        price: entry.price,
                    }
                }
            )
            setDummyMeals(transformedMeal)
        }
    )
}

const AvailableMeals = () => {




    const mealsList = dummyMeals.map((meal) => (
        <MealItem
            key={meal.id}
            id={meal.id}
            name={meal.name}
            description={meal.description}
            price={meal.price}
        />
    ));

    return (
        <section className={classes.meals}>
            <Card>
                <ul>{mealsList}</ul>
                <button onClick={fetchMenuHandler}>Fetch Meals</button>
            </Card>
        </section>
    );
};

export default AvailableMeals;
