import { Fragment,useState } from 'react';

import HeaderCartButton from './HeaderCartButton';
import mealsImage from '../../assets/meals.jpg';
import classes from './Header.module.css';


const Header = (props) => {
    const[isRegistered,setIsRegistered] =useState(false)

    function useEffectHandlers() {
        setIsRegistered(prevIsRegistered => !prevIsRegistered);
    }
  return (
    <Fragment>
      <header className={classes.header}>
        <h1>ReactMeals</h1>
          {!isRegistered && <button onClick={useEffectHandlers}>Register</button>}
          {isRegistered && <><p>Connected</p><button onClick={useEffectHandlers}>disConnect</button></>}
        <HeaderCartButton onClick={props.onShowCart} />
      </header>
      <div className={classes['main-image']}>
        <img src={mealsImage} alt='A table full of delicious food!' />
      </div>
    </Fragment>
  );
};

export default Header;
