import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';
import classes from './AvailableMeals.module.css';
import {useState} from "react";

const DUMMY_MEALS = [
    {
        id: 'm1',
        name: 'Sushi',
        description: 'Finest fish and veggies',
        price: 22.99,
    },
    {
        id: 'm2',
        name: 'Schnitzel',
        description: 'A german specialty!',
        price: 16.5,
    },
    {
        id: 'm3',
        name: 'Barbecue Burger',
        description: 'American, raw, meaty',
        price: 12.99,
    },
    {
        id: 'm4',
        name: 'Green Bowl',
        description: 'Healthy...and green...',
        price: 18.99,
    },
];


const AvailableMeals = () => {
    const [dummyMeals, setDummyMeals] = useState([])
    const fetchMenuHandler = () => {
        fetch('https://react2-f5ff0-default-rtdb.europe-west1.firebasedatabase.app/DUMMY_MEALS').then((response) => {
            return response.json();
        }).then(
            (data) => { console.log(data)

            }
        )
    }


    const mealsList = dummyMeals.map((meal) => (
        <MealItem
            key={meal.id}
            id={meal.id}
            name={meal.name}
            description={meal.description}
            price={meal.price}
        />
    ));

    return (
        <section className={classes.meals}>
            <Card>
                <ul>{mealsList}</ul>
                <button onClick={fetchMenuHandler}>Fetch Meals</button>
            </Card>
        </section>
    );
};

export default AvailableMeals;
