import { useState } from 'react';

import Header from './components/Layout/Header';
import Meals from './components/Meals/Meals';
import Cart from './components/Cart/Cart';
import CartProvider from './store/CartProvider';
import {FormRegister} from "./components/formOrder/FormRegister";

function App() {
  const [cartIsShown, setCartIsShown] = useState(false);
  const CartHandler = () => {
   setCartIsShown(previous => !previous)
  };

    const[isRegistered,setIsRegistered] =useState(false)

    function registeredHandlers() {
        setIsRegistered(prevIsRegistered => !prevIsRegistered);
    }


  return (
    <CartProvider>
        {isRegistered && <FormRegister></FormRegister>}
      {cartIsShown && <Cart onClose={CartHandler} /> }

      <Header onShowCart={CartHandler} onregisteredHandlers={registeredHandlers} isRegistered={isRegistered}/>
      <main>

        <Meals />
      </main>
    </CartProvider>
  );
}

export default App;
